using System;
using System.Configuration;

namespace OrdiOwin.Core.Settings
{    public class LoadedAssembliesSettings : ConfigurationSection    {        [ConfigurationProperty("", IsDefaultCollection = true)]        public AssemblyElementCollection AssemblyNames        {            get { return (AssemblyElementCollection)this[""]; }        }        public static LoadedAssembliesSettings GetSection()        {            return ConfigurationManager.GetSection("loadedAssemblies")                as LoadedAssembliesSettings;        }    }    public class AssemblyElementCollection : ConfigurationElementCollection    {        protected override ConfigurationElement CreateNewElement()        {            return new AssemblyElement();        }        protected override object GetElementKey(ConfigurationElement element)        {            AssemblyElement serviceTypeElement = (AssemblyElement)element;            return serviceTypeElement.AssemblyName;        }    }    public class AssemblyElement : ConfigurationElement    {        [ConfigurationProperty("assemblyName", IsRequired = true)]        public string AssemblyName        {            get { return (string)this["assemblyName"]; }            set { this["assemblyName"] = value; }        }    }
}
